}

generateCombination() {
  let combination = []
  for (let i = 0; i < 4; i++) {
    let random = Math.floor(Math.random()*6)
    let colors = ["red","blue","green","yellow","orange","purple"]
    combination.push(colors[i])
  }
  return combination;
}

  render() {
